export const computeChecksumValue = (code: string): number => {
    const codeClone = code
        .split('')
        .map((x) => Number.parseInt(x))
        .reverse();
    return codeClone.reduce((previousValue, value, index) => {
        return previousValue + value * (index + 1);
    }, 0);
};

export const validCheckSum = (checksum: number): boolean => {
    return checksum % 11 === 0;
};

import { computeChecksumValue, validCheckSum } from '../checksum';

// eslint-disable-next-line no-unused-vars
export type Validator = (code: string) => boolean;

export const illegalValidator: Validator = (code: string): boolean => {
    return !code.includes('?');
};

export const errorValidator: Validator = (code: string): boolean => {
    return validCheckSum(computeChecksumValue(code));
};

import { textHelpers } from '../config';
import { Writer } from '../writer/writer';

export interface Helper {
    print(): void;
}
export class CliHelper implements Helper {
    private writer: Writer;
    constructor(writer: Writer) {
        this.writer = writer;
    }

    print(): void {
