import { CliFunctionnality } from './parsing/command-parser';
import { errorValidator, illegalValidator } from './validation/validators';

export const OUTPUT_DIR = '';

export const DIGIT_WIDTH = 3;
export const DIGIT_HEIGHT = 4;
export const LINE_NUMBER_DIGIT = 9;
export const FILE_INDEX_IN_COMMAND = 2;
export const argsConfigured = new Map<CliFunctionnality, string>([
    [CliFunctionnality.INPUT_FILE, '-f'],
    [CliFunctionnality.OUTPUT_FILE, '-o'],
    [CliFunctionnality.CONSOLE_OUTPUT, '-c'],
    [CliFunctionnality.HELPER, '-h'],
]);
export const argsWithoutValues: CliFunctionnality[] = [
    CliFunctionnality.CONSOLE_OUTPUT,
    CliFunctionnality.HELPER,
];
export const validators = new Map([
    [' ILL', illegalValidator],
    [' ERR', errorValidator],
]);

export const textHelpers = [
    'usage: ./src/exec-improved-ocr.ts -f FileInputPath ...',
    'translate ocr files to number representation in three files Authorized Errored Unknown (you can specify multiple input files)\n',
    'usage: ./src/exec-improved-ocr.ts -f FilePath -o fileouputPath ...',
    'translate ocr file to number representation in the ouput file corresponding (you can specify multiple conbinations)\n',
    'usage: ./src/exec-improved-ocr.ts -c -f FilePath ...',
    'translate ocr file to number representation in the console (you can specify multiple input files)\n',
    'optional arguments:',
    '   -h                show this help message and exit',
    '   -c CONSOLE        specify to ouput in console',
    '   -f INPUT_FILE     specify the input file path with OCR text',
    '   -o OUTPUT_FILE    specify the ouput file path create or not',
];

import { Helper } from '../helpers/cli-helper';
import { ArgNotFound } from '../error/arg-not-found';

export enum CliFunctionnality {
    // eslint-disable-next-line no-unused-vars
    INPUT_FILE,
    // eslint-disable-next-line no-unused-vars
    OUTPUT_FILE,
    // eslint-disable-next-line no-unused-vars
    CONSOLE_OUTPUT,
    // eslint-disable-next-line no-unused-vars
    HELPER,
}

export class CommandParser {
    private argsParsed: Map<string, string[]>;
    private argsConfigured: Map<CliFunctionnality, string>;
    private argsWithoutValues: CliFunctionnality[];
    private helper: Helper;

    constructor(
        argsParsed: Map<string, string[]>,
        argsConfigured: Map<CliFunctionnality, string>,
        argsWithoutValues: CliFunctionnality[],
        helper: Helper
    ) {
        this.argsParsed = argsParsed;
        this.argsConfigured = argsConfigured;
        this.argsWithoutValues = argsWithoutValues;
        this.helper = helper;
    }

    parse(input: string): void {
        const formatedInput = this.formatInput(input);
        const inputSplit = formatedInput.split(' ');
        for (let i = 0; i < inputSplit.length; i += 2) {
            let argIsBoolean = false;
            for (const argWithoutValue of this.argsWithoutValues) {
                if (inputSplit[i] === this.argsConfigured.get(argWithoutValue)) {
                    this.mapBooleanArg(inputSplit[i]);
                    i -= 1;
                    argIsBoolean = true;
                }
            }
            if (!argIsBoolean) this.mapArgToValue(inputSplit[i], inputSplit[i + 1]);
        }
    }

    private formatInput(input: string): string {
        const re = /"([^"]*)"/gm;
        const stringParams = input.matchAll(re);
        let result = input;
        Array.from(stringParams).forEach((param) => {
            const paramMatched = param[1];
            const paramFormated = paramMatched.replace(/ /g, '$%&');
            result = result.replace(paramMatched, paramFormated);
        });
        return result.replace(/"/g, '');
    }

    private mapArgToValue(arg: string, value: string) {
        if (!this.isPresentInArgsConfigured(arg)) {
            this.helper.print();
            throw new ArgNotFound(`arg: ${arg} is not found`);
        }
        if (this.argsParsed.has(arg)) {
            this.argsParsed.get(arg)?.push(value);
        } else {
            this.argsParsed.set(arg, [value]);
        }
    }

    private mapBooleanArg(arg: string) {
        if (!this.isPresentInArgsConfigured(arg)) {
            this.helper.print();
            throw new ArgNotFound(`arg: ${arg} is not found`);
        }
        this.argsParsed.set(arg, []);
    }

    private isPresentInArgsConfigured(keyCandidate: string) {
        const argFound = Array.from(this.argsConfigured).find(
            (element: [CliFunctionnality, string]) => element[1] === keyCandidate
        );
        return argFound !== undefined;
    }

    public getArgsParsed(): Map<string, string[]> {
        return this.argsParsed;
    }
}

export class ArgNotFound extends Error {
    constructor(public message: string) {
        super(message);
    }
}

import { Writer } from './writer';

export class WriterInConsole implements Writer {
    async write(content: string[]): Promise<void> {
        process.stdout.write(content.join('\n') + '\n');
    }
}

import fs from 'fs/promises';
import { NoSuchFileOrDirectory } from '../error/no-such-file-or-directory';
import { OcrReferenceToNumber } from '../number-to-ocr-reference';

export class Parser {
    private digitWidth: number;
    private digitHeight: number;
    private lineNumberDigit: number;

    constructor(digitWidth: number, digitHeight: number, lineNumberDigit: number) {
        this.digitHeight = digitHeight;
        this.digitWidth = digitWidth;
        this.lineNumberDigit = lineNumberDigit;
    }

    public extractCodes(contentParse: string): string[] {
        const fileContent = contentParse.split('\n');
        const numberEntries = fileContent.length / this.digitHeight;
        const codesFromFile: string[] = [];

        for (let e = 0; e < numberEntries; e++) {
            const entry = this.extractEntry(fileContent, e);
            let codeFromLine = '';
            for (let i = 0; i < this.lineNumberDigit; i += 1) {
                const res = this.extractDigit(entry, this.digitWidth * i);
                const number = OcrReferenceToNumber.get(res);
                if (typeof number === 'undefined') {
                    codeFromLine += '?';
                } else {
                    codeFromLine += number.toString();
                }
            }
            codesFromFile.push(codeFromLine);
        }

        return codesFromFile;
    }

    private extractEntry(fileContent: string[], indexLine: number): string[] {
        const start = this.digitHeight * indexLine;
        return fileContent.slice(start, start + this.digitHeight);
    }

    private extractDigit(fileContent: string[], startIndex: number): string {
        let str = '\n';
        for (let i = 0; i < this.digitHeight; i += 1) {
            str += fileContent[i].slice(startIndex, this.digitWidth + startIndex);
            str += '\n';
        }
        return str;
    }
}

export const parse = async (pathToFile: string): Promise<string> => {
    try {
        return await fs.readFile(pathToFile, { encoding: 'utf-8' });
    } catch (error) {
        throw new NoSuchFileOrDirectory('file does not exist');
    }
};

export class NoSuchFileOrDirectory extends Error {
    constructor(public message: string) {
        super(message);
    }
}

import { DIGIT_WIDTH } from './config';
import { numberToOcrReference } from './number-to-ocr-reference';
import { Writer } from './writer/writer';

export const rowToOcrRepresentation = (code: number[]): string => {
    const ocr = code.map((number) => {
        return numberToOcrReference.get(number)?.split('\n').splice(1, 4) ?? '';
    });

    let strTotal = '';
    for (let e = 0; e < 4; e++) {
        let strLine = '';
        for (let i = 0; i < ocr.length; i++) {
            strLine += ocr[i][e];
        }
        if (e < 3) {
            strTotal += strLine + '\n';
        } else {
            strTotal += ' '.repeat(DIGIT_WIDTH * 9);
        }
    }

    return strTotal;
};

export const constrcutOneHundredOCREntries = async (writer: Writer) => {
    const str: string[] = [];
    for (let i = 0; i < 20; i++) {
        str.push(rowToOcrRepresentation([1, 2, 3, 4, 5, 6, 7, 8, 9]));
        str.push(rowToOcrRepresentation([1, 6, 3, 3, 5, 8, 7, 8, 9]));
        str.push(rowToOcrRepresentation([7, 3, 3, 3, 5, 8, 2, 8, 0]));
        str.push(rowToOcrRepresentation([7, 3, 4, 9, 5, 8, 2, 6, 0]));
        str.push(rowToOcrRepresentation([7, 1, 4, 1, 2, 8, 2, 6, 0]));
    }
    writer.write(str);
};

import { BadCommand } from '../error/output-and-input-didint';
import { Writer } from '../writer/writer';
import { WriterInFile } from '../writer/writer-in-file';

export interface ClassifyFile {
    // eslint-disable-next-line no-unused-vars
    write(paths: string[]): void;
}

export class SingleClassifyFile implements ClassifyFile {
    private parsedContent: string[][];
    private outputFiles: string[];

    constructor(parsedContent: string[][], outputFiles: string[]) {
        this.parsedContent = parsedContent;
        this.outputFiles = outputFiles;
        if (outputFiles.length != parsedContent.length) {
            throw new BadCommand('the number of inputs and outputs does not match');
        }
    }

    public async write(paths: string[]) {
        for (let i = 0; i < paths.length; i++) {
            const outputFile = this.outputFiles[i];
            const writer: Writer = new WriterInFile('w', outputFile);
            await writer.write(this.parsedContent[i]);
        }
    }
}

export class GroupClasifyFile implements ClassifyFile {
    private parsedContent: string[][];
    private outputDirectory: string;

    constructor(parsedContent: string[][], outputDirectory: string) {
        this.parsedContent = parsedContent;
        this.outputDirectory = outputDirectory;
    }

    private errorsSuffix: Map<string, string> = new Map([
        ['ILL', 'Unknown'],
        ['ERR', 'Errored'],
    ]);
    private authorisedPath = 'Authorized';

    async write(paths: string[]): Promise<void> {
        for (let i = 0; i < paths.length; i++) {
            await this.appendResult(this.parsedContent[i]);
        }
    }
    private async appendResult(lines: string[]) {
        for (const line of lines) {
            const errorsSuffix = Array.from(this.errorsSuffix.keys());
            for (const errorSuffix of errorsSuffix) {
                if (line.includes(errorSuffix)) {
                    const Writer = new WriterInFile(
                        'a',
                        this.outputDirectory + this.errorsSuffix.get(errorSuffix)
                    );
                    await Writer.write([line]);
                    return;
                }
            }
            const writer = new WriterInFile('a', this.outputDirectory + this.authorisedPath);
            await writer.write([line]);
        }
    }
}

export class BadCommand extends Error {
    constructor(public message: string) {
        super(message);
    }
}

import fs from 'fs/promises';
import { Writer } from './writer';

export class WriterInFile implements Writer {
    private mode: 'a' | 'w';
    private path: string;
    // eslint-disable-next-line no-unused-vars
    private modeToAction: Map<'a' | 'w', (path: string, content: string[]) => Promise<void>> =
        new Map([
            ['a', this.appendInFile.bind(this)],
            ['w', this.writeInFile.bind(this)],
        ]);

    constructor(mode: 'a' | 'w', path: string) {
        this.mode = mode;
        this.path = path;
    }

    async write(content: string[]): Promise<void> {
        const writeFunction = this.modeToAction.get(this.mode);
        if (!writeFunction) throw new Error('method not supported');
        await writeFunction(this.path, content);
    }

    private async writeInFile(path: string, content: string[]): Promise<void> {
        await fs.writeFile(path, content.join('\n'));
    }

    private async appendInFile(path: string, content: string[]): Promise<void> {
        await fs.appendFile(path, content.join('\n') + '\n', {
            flag: 'a+',
        });
    }
}

import { Validator } from './validators';

export class CodeToResult {
    private validators: Map<string, Validator>;
    constructor(validators: Map<string, Validator>) {
        this.validators = validators;
    }

    format(code: string): string {
        for (const [suffix, isValid] of this.validators) {
            if (!isValid(code)) {
                return code + suffix;
            }
        }
        return code;
    }
}
